import React, { useState }  from 'react'; 
import Board from '../Board/Board';
import './GameLogic.css';
import Status from './Status';

const GameLogic = () => {
    //Init state for board and player turn
    const [ boardCards, setBoardCards ] = useState(Array(9).fill(null));
    const [ playerXturn, setPlayerXturn] = useState(true); 

    //Player on click function
    const playerClickOnCard = (index) => {
        console.log(index)
        const cardsArr = [...boardCards]; 
        if(Status.someoneIsWinner(cardsArr) || cardsArr[index]){
            return; 
        }
        //put value in the arr depends on player
        cardsArr[index] = playerXturn ? 'X' : 'O'; 
        //update params
        setBoardCards(cardsArr); 
        setPlayerXturn(!playerXturn); 

    }

    //Reset Board
    const resetBoard = () => {
        setBoardCards(Array(9).fill(null));
        setPlayerXturn(true);
    }
    //Computer turn - random chooser
    const computerTurn = () => {
        if(!playerXturn){
            console.log("inside if inside computer turn")
            console.log(boardCards);

            //Chosing rand num while board is not full 
            let randNum = Math.floor(Math.random() * 10);
            do {
                randNum = Math.floor(Math.random() * 10);
            } while(!Status.isBoardFull(boardCards) && boardCards[randNum] !== null)   
            if(boardCards[randNum] === null){
                playerClickOnCard(randNum); 
            }else if (Status.isBoardFull(boardCards)){

            }else{
                
            }
        }
        return null;
    }

    //Game run
    const winner = Status.someoneIsWinner(boardCards); 
    let status = Status.checkStatus(winner, playerXturn); 
    const cp = computerTurn(); 
    console.log("computer chose = ", cp)
    return(
        <div>
            <div id={'status'}>{status}</div>
            <Board  id={`board` }
                    winner={winner}
                    playerClickOnCard={playerClickOnCard}
                    boardCards={boardCards}/>
            <button onClick={resetBoard}>Reset Board</button>
        </div>
    )
}
export default GameLogic;
