import React, { useState }  from 'react'; 
import Board from '../Board/Board';
import './GameLogic.css';
const confetti = require('canvas-confetti');

const GameLogic = () => {
    //Init state for board and player turn
    const [ boardCards, setBoardCards ] = useState(Array(9).fill(null));
    const [ playerXturn, setPlayerXturn] = useState(true); 
    //Player on click function
    const playerClickOnCard = (index) => {
        const cardsArr = [...boardCards]; 
        if(someoneIsWinner(cardsArr) || cardsArr[index]){
            return; 
        }
        cardsArr[index] = playerXturn ? 'X' : 'O'; 
        setBoardCards(cardsArr); 
        setPlayerXturn(!playerXturn); 
    }
    const someoneIsWinner = (boardCards) => {
        //Winner Combination
        const winnerCombination = [
            [0,1,2], //V rows first 
            [3,4,5], //V rows mid 
            [6,7,8],//V  rows last 
            [0,3,6],//V cols left
            [1,4,7], //V cols mid
            [2,5,8], //V  cols right
            [0,4,8], //V X left to right
            [2,4,6] //X X right to left 
        ]; 
        for (let i = 0; i < winnerCombination.length; i++) {
            const [ a, b, c ] = winnerCombination[i];
            if(boardCards[a] && 
                boardCards[a] === boardCards[b] && 
                boardCards[b] === boardCards[c]) {
                    confetti({
                        particleCount: 100,
                        spread: 70,
                        origin: { y: 0.6 }
                      });
                return boardCards[a];
            }
        }
        return false;
    }
    const winner = someoneIsWinner(boardCards); 
    let status = winner ? `Winner is ${winner}!` 
                        :`Player ${playerXturn ? 'X' : 'O'} turn`;
    return(
        <div>
            <div id={'status'}>{status}</div>
            <Board  id={`board` }
                    winner={winner}
                    playerClickOnCard={playerClickOnCard}
                    boardCards={boardCards}/>
            
        </div>
    )
}

export default GameLogic;
