import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import Status from './Status';

export default function SettingsModal({show , handleOnChangeModal, handleCloseModal, isAgainstRandom }) {
    const [ isRandomComputerChecked, setIsRandomComputerChecked ] = useState(true);
    const handleChange = (e) => {
        console.log("in child param id = ", e.target.id);
        if(e.target.id === 'randomComputer'){
            setIsRandomComputerChecked(true)
        }else{
            setIsRandomComputerChecked(false)
        }            
        handleOnChangeModal(!isRandomComputerChecked)
    }
    return (
      <Modal
        show={show}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
    >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Game Settings
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Computer Settings */}
            <Form>
                {'Computer: '}
                <Form.Check inline 
                            label="Random" 
                            type={'radio'} 
                            name={'computer'} 
                            id={'randomComputer'}
                            defaultChecked
                            onChange={handleChange}/>
                <Form.Check inline 
                            label="AI" 
                            type={'radio'} 
                            name={'computer'} 
                            id={'AIComputer'}
                            onChange={handleChange}/>
            </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={handleCloseModal}>Save</Button>
        </Modal.Footer>
      </Modal>
    );
  }
