import Status from './Status';
export default {
    availableSpotsOnArray(boardCards){
        let availableSpots = Array(); 
        for (let i = 0; i < boardCards.length; i++) {
            if(boardCards[i] >= 0 && boardCards[i] < 9){
                availableSpots.push(i)
            }
        }
        return availableSpots;
    },
    MinimaxFunctionForAi(newBoard, isComputer){
        console.log("inside minimax function")
        // Check if there are available spots on board
        let availableSpots = this.availableSpotsOnArray(newBoard);
        // console.log("NEW BOARD[0]",newBoard[0])
        if(Status.someoneIsWinner(newBoard) === 'X'){
            return { score: -10 };
        }else if(Status.someoneIsWinner(newBoard) === 'O'){
            return { score: 10 };
        }else if(availableSpots.length === 0){
            return{ score: 0 };            
        }
        let moves = [];
        for (let i = 0; i < availableSpots.length; i++) {
            let move = {}; 
            // console.log("newBoard[availableSpots[i]] = ", availableSpots[i])
            move['index'] = newBoard[availableSpots[i]];
            isComputer ? newBoard[availableSpots[i]] = 'O' : newBoard[availableSpots[i]] = 'X';
            if(isComputer){
                //Computer Player
                let result = MinimaxFunctionForAi(newBoard, false);
                move.score = result.score; 
            }else{
                //Human player
                let result = MinimaxFunctionForAi(newBoard, true);
                move.score = result.score;
            }
            newBoard[availableSpots[i]] = move.index; 
            moves.push(move);          
        }
        let bestMove; 
        if(isComputer){
            let bestScore = -1000;
            for (let i = 0; i < moves.length; i++) {
                if(moves[i].score > bestScore){
                    bestScore = moves[i].score;
                    bestMove = i;
                }                
            }
        }else{
            let bestScore = 1000;
            for (let i = 0; i < moves.length; i++) {
                if(moves[i].score < bestScore){
                    bestScore = moves[i].score;
                    bestMove = i;
                }                
            }
        }
        // console.log(moves);
        return moves[bestMove];
    }
}
