import React from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function SettingsModal({show ,handleCloseModal}) {
    return (
      <Modal
        show={show}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        top>
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Game Settings
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Computer Settings */}
          <p>
          <Form>
            {['checkbox', 'radio'].map(type => (
                <div key={`inline-${type}`} className="mb-3">
                <Form.Check inline label="1" type={type} id={`inline-${type}-1`} />
                <Form.Check inline label="2" type={type} id={`inline-${type}-2`} />
                <Form.Check
                    inline
                    disabled
                    label="3 (disabled)"
                    type={type}
                    id={`inline-${type}-3`}
                />
                </div>
            ))}
            </Form>
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={handleCloseModal}>Close</Button>
          <Button onClick={handleCloseModal}>Save</Button>
        </Modal.Footer>
      </Modal>
    );
  }
