import React from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function SettingsModal({show , handleOnChangeModal ,handleCloseModal, isAgainstRandom}) {
    return (
      <Modal
        show={show}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
    >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Game Settings
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Computer Settings */}
            <Form>
                {'Computer: '}
                <Form.Check inline 
                            label="Random" 
                            type={'radio'} 
                            name={'computer'} 
                            id={'randomComputer'}
                            check
                            onChange={handleOnChangeModal}/>
                <Form.Check inline 
                            label="AI" 
                            type={'radio'} 
                            name={'computer'} 
                            id={'AIComputer'}
                            onChange={handleOnChangeModal}/>
            </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={handleCloseModal}>Save</Button>
        </Modal.Footer>
      </Modal>
    );
  }
