import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function SettingsModal({show , handleOnChangeModal, handleCloseModal, isAgainstRandom }) {
    console.log("inside modal  is against random player= ", isAgainstRandom);
    const [ isRandomComputerChecked, setIsRandomComputerChecked ] = useState(isAgainstRandom);
    const handleChange = (e) => {
        console.log("in child param id = ", e);
        if(e.target.id === 'randomComputer'){
            // setIsRandomComputerChecked(true)
            handleOnChangeModal(true);
        }else{
            // setIsRandomComputerChecked(false)
            handleOnChangeModal(false);
        }            
        // handleOnChangeModal(isRandomComputerChecked)
    }
    return (
      <Modal
        show={show}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
    >
        <Modal.Header>
          <Modal.Title id="contained-modal-title-vcenter">
            Game Settings
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Computer Settings */}
            <Form>
                {'Computer: '}
                <Form.Check inline 
                            label="Random" 
                            type={'radio'} 
                            name={'computer'} 
                            id={'randomComputer'}
                            checked={isAgainstRandom? true: false}
                            onChange={handleOnChangeModal}/>
                <Form.Check inline 
                            label="AI" 
                            type={'radio'} 
                            name={'computer'} 
                            id={'AIComputer'}
                            checked={isAgainstRandom ? false: true }
                            onChange={handleOnChangeModal}/>
                {'Who Start: '}
                <Form.Check inline 
                            label="Computer" 
                            type={'radio'} 
                            name={'computer'} 
                            id={'startComputer'}
                            checked={isAgainstRandom? true: false}
                            onChange={handleOnChangeModal}/>
                <Form.Check inline 
                            label="Me" 
                            type={'radio'} 
                            name={'me'} 
                            id={'startMe'}
                            checked={isAgainstRandom ? false: true }
                            onChange={handleOnChangeModal}/>
            </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={handleCloseModal}>Save</Button>
        </Modal.Footer>
      </Modal>
    );
  }
