import React, { useState }  from 'react'; 
import Board from '../Board/Board';
import './GameLogic.css';
import Status from './Status';
import SettingsModal from './SettingsModal';
import AIComputer from './AIComputer';

const GameLogic = () => {
    //Init state for board and player turn
    const [ boardCards, setBoardCards ] = useState(Array(9).keys());
    const [ isAgainstRandom, setIsAgainstRandom] = useState(true);
    const [ playerXturn, setPlayerXturn] = useState(true); 
    const [ settingsModal, setSettingsModal ] = useState(false);
    
    //Player on click function
    const playerClickOnCard = (index) => {
        const cardsArr = [...boardCards]; 
        if(Status.someoneIsWinner(cardsArr) || cardsArr[index]){
            return; 
        }
        //put value in the arr depends on player
        cardsArr[index] = playerXturn ? 'X' : 'O'; 
        //update params
        setBoardCards(cardsArr); 
        setPlayerXturn(!playerXturn); 
    }

    //Reset Board
    const resetBoard = () => {
        setBoardCards(Array(9).keys());
        setPlayerXturn(true);
    }

    //Game Settings
    const gameSettings = () => {
        console.log("inside game settings button")
        setSettingsModal(true);
    }
    const handleCloseModal = () => {
        setSettingsModal(false);
    }
    const handleOnChangeModal = ( fromChild ) => {
        console.log("param parent is against random = ", isAgainstRandom)
        console.log("inside on change handle modal, param from child = ", fromChild ); 
        setIsAgainstRandom(fromChild);
    }
    //Computer turn 
    const computerTurn = () => {
        (isAgainstRandom) ? playerClickOnCard(RandomComputerTurn()) : playerClickOnCard(AIComputerTurn());
    }
    //Random computer chose
    const RandomComputerTurn = () => {
        if(!playerXturn && !Status.isBoardFull(boardCards)){
            //Choosing rand num while board is not full 
            let randNum = Math.floor(Math.random() * 10);
            do {
                randNum = Math.floor(Math.random() * 10);
            } while(!Status.isBoardFull(boardCards) && boardCards[randNum] >=  0 && boardCards[randNum] < 9)   
            return (randNum); 
        }
        return null;
    }
    // Computer Turn - AI computer 
    const AIComputerTurn = () => {
        console.log("inside AI computer turn function");
        // Minimax function need to return the index that the computer turn 
        let toReturn = MinimaxFunctionForAi(boardCards, true)
        console.log("RESULT FROM AI = ", toReturn);
        return toReturn.index;
    }
    const MinimaxFunctionForAi = (newBoard, isComputer) => {
        // Check if there are available spots on board
        let availableSpots = AIComputer.availableSpotsOnArray(newBoard);
        console.log("NEW BOARD[0] = ",newBoard[0])
        if(Status.someoneIsWinner(newBoard) === 'X'){
            return { score: -10 };
        }else if(Status.someoneIsWinner(newBoard) === 'O'){
            return { score: 10 };
        }else if(availableSpots.length === 0){
            return{ score: 0 };            
        }
        
        let moves = [];
        for (let i = 0; i < availableSpots.length; i++) {
            let move = {}; 
            console.log("newBoard[availableSpots[i]] = ", availableSpots[i])
            move['index'] = newBoard[availableSpots[i]];
            isComputer ? newBoard[availableSpots[i]] = 'O' : newBoard[availableSpots[i]] = 'X';
            if(isComputer){
                //Human player
                let result = MinimaxFunctionForAi(newBoard, false);
                move.score = result.score; 
            }else{
                //Computer Player
                let result = MinimaxFunctionForAi(newBoard, true);
                move.score = result.score;
            }
            newBoard[availableSpots[i]] = move.index; 
            moves.push(move);          
        }
        let bestMove; 
        if(!isComputer){
            let bestScore = -1000;
            for (let i = 0; i < moves.length; i++) {
                if(moves[i].score > bestScore){
                    bestScore = moves[i].score;
                    bestMove = i;
                }                
            }

        }else{
            let bestScore = 1000;
            for (let i = 0; i < moves.length; i++) {
                if(moves[i].score < bestScore){
                    bestScore = moves[i].score;
                    bestMove = i;
                }                
            }
        }
        // console.log(moves);
        return moves[bestMove];
    }

    //Game run
    const winner = Status.someoneIsWinner(boardCards); 
    let status = Status.checkStatus(winner, playerXturn); 
    if(!playerXturn){
        //computer Turn
        computerTurn();
    } 
    return(
        <div>
            <div id={'status'}>{status}</div>
            <div id={'gameButtonsWrapper'}>
                <button className={'gameButtons'} onClick={gameSettings}>Settings</button>
                <button className={'gameButtons'} onClick={resetBoard}>Reset Board</button>
            </div>
            <Board  id={`board` }
                    winner={winner}
                    playerClickOnCard={playerClickOnCard}
                    boardCards={boardCards}/> 
            {/* Modal for Settings */}
            {settingsModal ? <SettingsModal 
                                    show={settingsModal} 
                                    handleOnChangeModal={handleOnChangeModal} 
                                    handleCloseModal={handleCloseModal}
                                    isAgainstRandom={isAgainstRandom}/> 
                            : null}
        </div>

    )
}
export default GameLogic;
