import React from 'react';
import './board.css';
import Card from '../Card/Card';

const Board = ({ cards }) =>{
    const renderCard = (num) =>{
        return <Card value={cards[num]} 
                    onClick={() => onClick(num)}/>
    }
    return(
        <div> 
           <div className={'board-row'}>
               {renderCard(0)}
               {renderCard(1)}
               {renderCard(2)}
           </div>
           <div className={'board-row'}>
               {renderCard(3)}
               {renderCard(4)}
               {renderCard(5)}
           </div>
           <div className={'board-row'}>
               {renderCard(6)}
               {renderCard(7)}
               {renderCard(8)}
           </div>
        </div>
    )
}

export default Board;
