import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

export default function SettingsModal({show , handleOnChangeModal ,handleCloseModal }) {
    const [ isRandomComputerChecked, setIsRandomComputerChecked ] = useState(true);
    const handleChange = (e) => {
        console.log("in child", e.target)
        e.target.id === 'randomComputer'
            ? setIsRandomComputerChecked(true)
            : setIsRandomComputerChecked(false)
        console.log("is random = ", isRandomComputerChecked);
        
        return handleOnChangeModal(isRandomComputerChecked)
    }
    return (
      <Modal
        show={show}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
    >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Game Settings
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          {/* Computer Settings */}
            <Form>
                {'Computer: '}
                <Form.Check inline 
                            label="Random" 
                            type={'radio'} 
                            name={'computer'} 
                            id={'randomComputer'}
                            defaultChecked={isRandomComputerChecked}
                            onChange={handleChange}/>
                <Form.Check inline 
                            label="AI" 
                            type={'radio'} 
                            name={'computer'} 
                            id={'AIComputer'}
                            defaultChecked={!isRandomComputerChecked}
                            onChange={handleChange}/>
            </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={handleChange}>Save</Button>
        </Modal.Footer>
      </Modal>
    );
  }
